import { useContext, useState } from 'react'
import './write.css'
import axios from 'axios'
import {Context} from '../../context/Context';

export default function Write() {

    const [title, setTitle] = useState("")
    const [desc, setDesc] = useState("")
    const [file, setFile] = useState(null)
    const {user} = useContext(Context)

    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = {
            username:user.username,title,desc,file
        }
        axios.post("/posts",newPost)
    }

    return (
        <div className="write">
            <img className="writeImg" src="https://images.pexels.com/photos/6685428/pexels-photo-6685428.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500" alt="" />
            <form className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                        <i className="writeIcon fas fa-plus"></i>
                    </label>
                    <input type="file" id="fileInput" style={{display:'none'}}/>
                    <input type="text" placeholder="Başlık" className="writeInput" autoFocus={true} />
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder="Hikayenizi anlatın..." type="text" className="writeInput writeText"></textarea>
                </div>
                <button className="writeSubmit" type="submit">Yayınla</button>
            </form>
        </div>
    )
}
