import './settings.css'
import Sidebar from '../../components/sidebar/Sidebar'
import { Context } from '../../context/Context';
import { useContext } from 'react';

export default function Settings() {

  const {user,dispatch} = useContext(Context);
  const [file, setFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newPost = {
      username: user.username,
      title,
      desc,
      file,
    };
    if (file) {
      const data = new FormData();
      const fileName = Date.now() + file.name;
      data.append("name", fileName);
      data.append("file", file);
      newPost.photo = fileName;
      try {
        await axios.post("/upload", data);
      } catch (error) {}
    }
    try {
      const res = await axios.post("/posts", newPost);
      window.location.replace("/post/" + res.data.data._id);
    } catch (error) {}
  };
  
    return (
        <div className="settings">
            <div className="settingsWrapper">
                <div className="settingsTitle">
                    <span className="settingsUpdateTitle">Hesabı Güncelle</span>
                    <span className="settingsDeleteTitle">Hesabı Sil</span>
                </div>
                <form className="settingsForm">
                    <label>Profil Resmi</label>
                    <div className="settingsPP">
                        <img src={user.profilePic} alt="" />
                        <label htmlFor="fileInput">
                            <i className="settingsPPIcon far fa-user-circle"></i>
                        </label>
                        <input type="file" id="fileInput" style={{display:'none'}}/>
                    </div>
                    <label>Kullanıcı Adı</label>
                    <input type="text" placeholder="Taha Kazan" />
                    <label>Email</label>
                    <input type="email" placeholder="tahakazantk@gmail.com" />
                    <label>Şifre</label>
                    <input type="password"/>
                    <button className="settingsSubmit">Güncelle</button>
                </form>
            </div>
            <Sidebar/>
        </div>
    )
}
